  selectWithParent("#caption_div").append("textarea").attr({
    cols: 37,
    rows: 5,
    placeholder: "Caption text",
    class: "text_input",
    maxlength: 140,
    id: "add_caption_text_input"
  });

  selectWithParent("#caption_div").append("input").attr({
    type: "image",
    name: "Add Caption",
    id: "add_caption_btn",
    class: "img_btn_enabled",
